import { Typography } from 'antd';
import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { useParams } from 'react-router';
import { Wrapper, Img, ProductDetails } from './style';
const { Title, Text } = Typography;
const ProductDetail = () => {
  const { id } = useParams();
  const { products } = useSelector((store) => store.product);

  const [loading, setLoading] = useState(true);
  const [productDetail, setproductDetail] = useState('');

  useEffect(() => {
    setproductDetail(products.find((product) => product._id === id));
    setLoading(false);
  }, [products, id]);
  console.log(productDetail);
  return (
    <Wrapper>
      <div>
        <Img src={productDetail.image} alt={productDetail.name} />
      </div>
      <ProductDetails>
        <Title>{productDetail.name}</Title>
        <Text>{productDetail.description}</Text>
        <Text>{productDetail.price}</Text>
      </ProductDetails>
      <div>
        Lorem ipsum dolor sit amet consectetur adipisicing elit. Amet, vero natus. Praesentium vitae
        similique quasi tempora ipsum voluptas minima officiis sed consectetur, sunt vel, veniam
        animi neque iste excepturi repellendus.
      </div>
    </Wrapper>
  );
};

export default ProductDetail;
