import Grid from 'antd/lib/card/Grid';
import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { useParams } from 'react-router';

const ProductDetail = () => {
  const { id } = useParams();
  const { products } = useSelector((store) => store.product);

  const [loading, setLoading] = useState(true);
  const [productDetail, setproductDetail] = useState('');

  useEffect(() => {
    setproductDetail(products.find((product) => product._id === id));
    setLoading(false);
  }, [products, id]);
  console.log(productDetail);
  return (
    <div>
      <Grid container direction='row' justify='center' alignItems='center'>
        <Grid item>
          <img src={productDetail.image} alt={productDetail.name} />
        </Grid>
        <Grid container item>
          <Grid>{productDetail.name}</Grid>
          <Grid>{productDetail.description}</Grid>
          <Grid>{productDetail.price}</Grid>
        </Grid>
        <Grid item>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Amet, vero natus. Praesentium
          vitae similique quasi tempora ipsum voluptas minima officiis sed consectetur, sunt vel,
          veniam animi neque iste excepturi repellendus.
        </Grid>
      </Grid>
    </div>
  );
};

export default ProductDetail;
