import { GET_PRODUCT } from '../Types/types';

const initialState = {
  products: [],
};
export default function getProductReducer(state = initialState, action) {
  switch (action.type) {
    case GET_PRODUCT:
      return { ...state, products: action.payload };

    default:
      return state;
  }
}
