import React, { useEffect } from 'react';
import ProductCard from './ProductCard/ProductCard';
import { Container, Wrapper } from './styled';
import { Typography } from 'antd';
import { products } from '../../ExampleData/Product';
import { db } from '../../Firebase/Firebase';
import Axios from 'axios';
const { Title } = Typography;

const ShoppingsProducts = () => {
  const getProducts = async () => {
    //  const res = await db.collection('Products').onSnapshot((querySnapshot) => {
    //      const docs = [];
    //      querySnapshot.forEach((doc) => {
    //          docs.push(...doc.data)
    //      })
    //});
    const res = await db.collection('Products').onSnapshot((res) => {
      res.forEach((doc) => {
        console.log(doc.data());
      });
    });

    console.log(res);
  };
  useEffect(() => {
    getProducts();
  }, []);
  return (
    <Container>
      <Title type='secondary'> Productos </Title>
      <Wrapper>
        {products.map((product, i) => (
          <div key={i}>
            <ProductCard
              image={product.image}
              name={product.name}
              price={product.price}
              description={product.description}
            />
          </div>
        ))}
      </Wrapper>
    </Container>
  );
};

export default ShoppingsProducts;
